};
import React, { useState } from "react";
import PropTypes from "prop-types";
import classNames from "classnames";
import { Component } from "./AccordionItem.style";
import { ThemeProvider } from "styled-components";
import { theme } from "./../../../theme";

const AccordionItem = (props) => {
  const [opened, setOpened] = useState(false);
  const { title, content } = props;
  const accordionItemClassName = classNames(
    "accordion",
    opened ? "accordion-item--opened" : false
  );
  const handleAccordionToggle = () => {
    setOpened(!opened);
  };

  let accordionItemComp = (
    <ThemeProvider theme={theme}>
      <Component
        className={accordionItemClassName}
        onClick={handleAccordionToggle}
      >
        <div className="accordion-item__line">
          <h3 className="accordion-item__title">{title}</h3>
          <span className="accordion-item__icon" />
        </div>
        <div className="accordion-item__inner">
          <div className="accordion-item__content">
            <p className="accordion-item__paragraph">{content}</p>
          </div>
        </div>
      </Component>
    </ThemeProvider>
  );
  return accordionItemComp;
};

AccordionItem.defaultProps = {};

AccordionItem.propTypes = {
  className: PropTypes.string,
  onClick: PropTypes.func,
};
export default AccordionItem;
import styled, { css } from "styled-components";

export const Component = styled.div`
  width: 100%;
  ${(props) =>
    props.hideIcon
      ? css`
          .accordion-item__icon {
            display: none;
          }
        `
      : null};
  .accordion-list {
    list-style: none;
    margin: 0;
    padding: 0;
    overflow: hidden;
    border: 1px solid #f4f4f4;
  }
  .accordion-list__item + .accordion-list__item {
    border-top: 1px solid #f4f4f4;
  }
  .accordion-item--opened .accordion-item__icon {
    transform: rotate(180deg);
  }
  .accordion-item--opened .accordion-item__inner {
    max-height: 100rem;
    transition-timing-function: cubic-bezier(0.895, 0.03, 0.685, 0.22);
    transition-duration: 0.5s;
    transition-property: max-height;
  }
  .accordion-item--opened .accordion-item__content {
    opacity: 1;
    transform: translateY(0);
    transition-delay: 0.2s;
    transition-timing-function: ease-in-out;
    transition-duration: 0.2s;
    transition-property: opacity, transform;
  }
  .accordion-item__line {
    display: block;
    padding: 0.8rem 1.2rem;
    display: flex;
    justify-content: space-between;
    align-items: center;
    background-color: #fff;
    z-index: 2;
    position: relative;
  }
  .accordion-item__title {
    font-size: 1.2rem;
    margin: 0;
    font-weight: 500;
    color: #121212;
  }
  .accordion-item__icon {
    width: 1.5rem;
    height: 1.5rem;
    transition: transform 0.3s ease-in-out;
    background-size: contain;
    background-repeat: no-repeat;
    background-image: url(data:image/png;base64,);
    opacity: 0.6;
  }
  .accordion-item__inner {
    max-height: 0;
    overflow: hidden;
    text-transform: cubic-bezier(0.95, 0.05, 0.795, 0.035);
    transition-duration: 0.5s;
    transition-property: max-height;
    z-index: 1;
    position: relative;
  }
  .accordion-item__content {
    opacity: 0;
    transform: translateY(-1rem);
    transition-timing-function: linear, ease;
    transition-duration: 0.1s;
    transition-property: opacity, transform;
    transition-delay: 0.5s;
    padding: 0 1.2rem 1.2rem;
  }
  .accordion-item__paragraph {
    margin: 0;
    font-size: 1rem;
    color: #333;
    font-weight: 300;
    line-height: 1.3;
  }
`;
import React from "react";
import PropTypes from "prop-types";
import classNames from "classnames";
import AccordionItem from "./AccordionItem";
import { Component } from "./Accordion.style";
import { ThemeProvider } from "styled-components";
import { theme } from "./../../theme";

const Accordion = (props) => {
  const {
    data,
    accordionClass,
    accordionHeaderClass,
    accordionBodyClass,
    hideIcon,
  } = props;
  const accordionClassName = classNames("wrapper", accordionClass);
  return (
    <ThemeProvider theme={theme}>
      <Component className={accordionClassName} hideIcon={hideIcon}>
        <ul className="accordion-list">
          {data.map((data, key) => {
            return (
              <li className="accordion-list__item" key={key}>
                <AccordionItem
                  {...data}
                  accordionHeaderClass={accordionHeaderClass}
                  accordionBodyClass={accordionBodyClass}
                />
              </li>
            );
          })}
        </ul>
      </Component>
    </ThemeProvider>
  );
};

AccordionItem.propTypes = {
  accordionClass: PropTypes.string,
  accordionHeaderClass: PropTypes.string,
  accordionBodyClass: PropTypes.string,
  hideIcon: PropTypes.bool,
  tag: PropTypes.string,
};
AccordionItem.defaultProps = {
  tag: "div",
};

export default Accordion;

export { Accordion as CDBAccordion };
import styled from "styled-components";
import { colorStyle } from "styled-system";

export const Component = styled.div`
    ${colorStyle}
    border-radius: 0px;
    align-items:center;
    justify-content:space-between;
    min-width:500px;
`;
export const Button = styled.button`
	color:inherit;
	border:none;
	background-color:transparent;
    right: 0px;
    position: absolute;
    padding: 0px 1.2em;
	:focus { outline:none;	}
`;
import React from "react";
import classNames from "classnames";
import PropTypes from "prop-types";
import { ThemeProvider } from "styled-components";
import { theme } from "./../../theme";
import { Component, Button } from "./Alert.style";

const Alert = (props) => {
  const { className, tag, color, children, dismiss, ...attributes } = props;

  const alertClasses = classNames("alert", className);

  const close = (e) => {
    e.target.parentNode.remove();
  }

  let alertComponent = (
    <ThemeProvider theme={theme}>
    <Component {...attributes} className={alertClasses} role="alert" as={tag} colors={color} >
      {children}
      { dismiss ? 
        <Button onClick={close} >&times;</Button>
        :
        null
      }
    </Component>
    </ThemeProvider>
  );

  return alertComponent;
};

Alert.defaultProps = {
  color: "primary",
  tag: "div",
};

Alert.propTypes = {
  className: PropTypes.string,
  color: PropTypes.oneOf([
    "primary",
    "secondary",
    "success",
    "danger",
    "warning",
    "info",
    "dark",
    "light"
  ]),
  onClose: PropTypes.func,
  onClosed: PropTypes.func,
  tag: PropTypes.string,
};

