const DropdownToggle = (props) => {
  const { isOpen } = useContext(DropDownContext);
  const [isOpenValue, setIsOpenValue] = isOpen;

  const {
    className,
    tag,
    color,
    children,
    caret,
    nav,
    size,
    disabled,
    circle,
    dropleft,
    dropright,
    dropup,
    dropdown,
    ...attributes
  } = props;
  const dropdownToggleClasses = classNames(
    {
      "nav-link": nav,
    },
    className
  );
  const toggleDropdown = (e) => {
    const { disabled, nav, tag, onClick } = props;

    if (disabled) {
      return;
    }

    if (nav && !tag) {
      return;
    }

    if (onClick) {
      onClick(e);
    }

    setIsOpenValue(!isOpenValue);
  };
  const caretClasses = classNames(
    dropleft && "mr-3",
    dropright || dropdown || dropup ? "ml-3" : "",
    className
  );
  const CaretIcon = caret ? (
    <Caret
      className={caretClasses}
      dropleft={dropleft}
      dropright={dropright}
      dropdown={dropdown}
      dropup={dropup}
      {...attributes}
    >
      <span className="pro-arrow-wrapper">
        <span className="pro-arrow" />
      </span>
    </Caret>
  ) : null;

  let dropdownToggleComponent = (
    <ThemeProvider theme={theme}>
      <Reference>
        {({ ref }) => (
          <Component
            className={dropdownToggleClasses}
            ref={ref}
            as={tag}
            colors={color}
            size={size}
            disabled={disabled}
            circle={circle}
            onClick={() => {
              toggleDropdown();
            }}
          >
            {dropleft && CaretIcon}
            {children}
            {dropright || dropdown || dropup ? CaretIcon : null}
          </Component>
        )}
      </Reference>
    </ThemeProvider>
  );

  return dropdownToggleComponent;
};

DropdownToggle.defaultProps = {
  "aria-haspopup": true,
  color: "primary",
  tag: "button",
};

DropdownToggle.propTypes = {
