    const popperClasses = classNames(
      (material || email) && 'tooltip-inner',
      material && (sm ? 'md-inner' : 'md-inner-main'),
      email && (sm ? 'md-inner' : 'md-inner-email')
    )

    return (
      <ThemeProvider theme={theme}>
        <Fragment>
          {!domElement ? (
            <Wrapper.type
              {...Wrapper.props}
              onMouseEnter={() => !clickable && this.doToggle(true)}
              onMouseLeave={() =>
                !clickable &&
