import React, { useState } from "react";
import PropTypes from "prop-types";
import classNames from "classnames";
import { Component } from "./Switch.style";
import { ThemeProvider } from 'styled-components'
import { theme } from './../../theme'

const Switch = (props) => {
  const { checked, className, ...attributes } = props;
  const [isChecked, setIsChecked] = useState(checked);
  const switchClassName = classNames(className);
  const handleChange = () => {
    setIsChecked(!isChecked);
  };

  return (
    <ThemeProvider theme={theme}>
    <Component className={switchClassName} {...attributes}>
      <div className="switch-container">
        <label>
          <input
            checked={isChecked}
            onChange={handleChange}
            className="switch"
            type="checkbox"
          />
          <div>
            <div></div>
          </div>
        </label>
      </div>
    </Component>
    </ThemeProvider>
  );
};

Switch.propTypes = {
  checked: PropTypes.bool,
  tag: PropTypes.string,
};
Switch.defaultProps = {
  tag: "div",
  checked: false,
};

export default Switch;

export { Switch as CDBSwitch };
import React from "react";
import PropTypes from "prop-types";
import classNames from "classnames";
import { ThemeProvider } from "styled-components";
import { theme } from "./../../../theme";

const TableHeader = (props) => {
  const { children, color, columns, textWhite, ...attributes } = props;

  const isTheadColor = color === "dark" || color === "light";
  const classes = classNames({
    "text-white": textWhite,
    [`thead-${color}`]: color && isTheadColor,
    [`${color}`]: color && !isTheadColor,
  });

  let tableHeaderComponent = (
    <ThemeProvider theme={theme}>
      <thead data-test="table-head" {...attributes} className={classes}>
        {columns && (
          <tr>
            {columns.map((col) => (
              <th
                key={col.field}
                className={
                  col.hasOwnProperty("minimal") ? `th-${col.minimal}` : ""
                }
              >
                {col.label}
              </th>
            ))}
          </tr>
        )}
        {children}
      </thead>
    </ThemeProvider>
  );

  return tableHeaderComponent;
};

TableHeader.propTypes = {
  children: PropTypes.node,
  color: PropTypes.string,
  columns: PropTypes.arrayOf(PropTypes.object),
  textWhite: PropTypes.bool,
};

TableHeader.defaultProps = {
  textWhite: false,
};

export default TableHeader;
export { TableHeader as CDBTableHeader };
